import React, { useState, useEffect, useRef  } from "react";
import axios from 'axios';
import { Database } from "../../API/API";

function _MAIN() {

    const [DETAIL_LIST, SET_DETAIL_LIST] = useState([])

    const GET_DETAIL = () => {

        axios.get(`${Database}Name-list/Name-list.php`).then(data => {

            SET_DETAIL_LIST(data.data);

        });

    }

    const ADD_DETAIL = () => {

        let FORM_DATA = new FormData(document.getElementById('form-add'));

        console.log(`${Database}Add-name/Add-name.php`)
        axios({
            method: "post",
            url: `${Database}Add-name/Add-name.php`,
            data: FORM_DATA,
            headers: { "Content-Type": "multipart/form-data" }
        }).then(data => {

            console.log(data.data)

        });

    }

    useEffect(() => {

        setInterval(() => {

            GET_DETAIL();

        }, 1000);

    }, []);

    return (
        <div>
            <form id='form-add'>
                <input type="text" name='Name' placeholder="add name"/>
                <button type="button" onClick={() => ADD_DETAIL()}>Add</button>
            </form>
            <br/>
            <h3>User list</h3>
            {DETAIL_LIST.map((i, key) => (
                <div key={key}>
                    - {i.u_name}
                    <br/>
                </div>
            ))}
        </div>
    );
}

export default _MAIN;
